import React from 'react';
import './styles/DrumPad.css';


export default class DrumPad extends Component {
    constructor(props) {
        super(props);
        this.state = {
            padStyle: inactiveStyle
        }
        this.playSound = this.playSound.bind(this);
        this.handleKeyPress = this.handleKeyPress.bind(this);
        this.activatePad = this.activatePad.bind(this);  
    }
    componentDidMount() {
        document.addEventListener('keydown', this.handleKeyPress);
    }
    componentWillUnmount() {
        document.removeEventListener('keydown', this.handleKeyPress);
    }
    handleKeyPress(e) {
        if (e.keyCode === this.props.keyCode) {
            this.playSound()
        }
    }



    activatePad(){
        if (this.props.power) {
            this.state.padStyle.backgroundColor === "var(--color-aqua)"
              ? this.setState({
                  padStyle: inactiveStyle,
                })
              : this.setState({
                  padStyle: activeStyle,
                });
        } else {
            this.state.padStyle.marginTop === 13 ? this.setState({
                padStyle: inactiveStyle
            }) : this.setState({
                padStyle: {
                    height: 77,
                    marginTop: 13,
                    backgroundColor: 'grey',
                    boxShadow: "0 3px grey"
                }
            });
        }
    }
    playSound(e) {
        const sound = document.getElementById(this.props.padLetter);
        sound.currentTime = 0;
        sound.play();
        this.activatePad();
        setTimeout(() => this.activatePad(), 100);

        this.props.updateDisplay(this.props.clipId.replace(/-/g, ' '));
    }
    render() {
        return (
            <div id={this.prop.clipId} onClick={this.playSound} className="drum-pad" style={this.state.padStyle}>
                <audio className='clip' id={this.props.padLetter} src={this.props.clip}></audio>
                {this.props.padLetter}
            </div>
        )
    }

}
